#include <cstring>
using namespace std;


const int INF = 1e9+7;
const int MAX = 1e5+5;

int n, k;
int h[MAX];

int solve(int pos) {
  if(pos > n) return INF;
  if(pos == n) return 0;

  int cost = INF;

  for(int i = 1; i <= k; i++) {
    if(pos + i <= n) {
      int costk = abs(h[pos + i] - h[pos]);
      cost = min(cost, costk + solve(pos + i));
    }
  }

  return cost;
}

int main() {
    // do not remove this code if you use cin or cout
    ios::sync_with_stdio(false);
    cin.tie(0);

    cin>>n>>k;

    for(int i = 1; i <= n; i++) cin>>h[i];

    cout<<solve(1)<<endl;

    return 0;
}

